import React, { useState } from 'react';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';
import '../styles/FacultyDashboard.css';

ChartJS.register(ArcElement, Tooltip, Legend);

const FacultyDashboard = () => {
  const [activeSection, setActiveSection] = useState('dashboard');
  const [showApplicationsModal, setShowApplicationsModal] = useState(false);
  const [showReportsModal, setShowReportsModal] = useState(false);
  const [showStudentsModal, setShowStudentsModal] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [studentReports, setStudentReports] = useState(null);

  // Student data
  const students = [
    { 
      id: 1, 
      name: 'Arpanjoot Kour', 
      course: 'Computer Science', 
      year: '3rd Year',
      gpa: 3.8,
      internshipStatus: 'Active',
      performance: {
        technical: 92,
        communication: 88,
        problemSolving: 90,
        teamwork: 95
      },
      internshipDetails: {
        company: 'Tech Solutions Inc.',
        position: 'Software Development Intern',
        startDate: '2024-01-15',
        endDate: '2024-06-15',
        mentor: 'Dr. Smith'
      }
    },
    { 
      id: 2, 
      name: 'Vansh Raina', 
      course: 'Electrical Engineering', 
      year: '2nd Year',
      gpa: 3.6,
      internshipStatus: 'Pending',
      performance: {
        technical: 85,
        communication: 82,
        problemSolving: 88,
        teamwork: 90
      },
      internshipDetails: {
        company: 'Power Systems Ltd.',
        position: 'Electrical Engineering Intern',
        startDate: '2024-05-01',
        endDate: '2024-08-31',
        mentor: 'Dr. Johnson'
      }
    },
    { 
      id: 3, 
      name: 'Angela Mary Promod', 
      course: 'Mechanical Engineering', 
      year: '4th Year',
      gpa: 3.9,
      internshipStatus: 'Completed',
      performance: {
        technical: 95,
        communication: 90,
        problemSolving: 92,
        teamwork: 88
      },
      internshipDetails: {
        company: 'Mechanical Innovations',
        position: 'Mechanical Design Intern',
        startDate: '2023-06-01',
        endDate: '2023-12-31',
        mentor: 'Dr. Williams'
      }
    },
    { 
      id: 4, 
      name: 'Omkar Desai', 
      course: 'Civil Engineering', 
      year: '3rd Year',
      gpa: 3.7,
      internshipStatus: 'Active',
      performance: {
        technical: 88,
        communication: 85,
        problemSolving: 90,
        teamwork: 92
      },
      internshipDetails: {
        company: 'Civil Structures Inc.',
        position: 'Structural Engineering Intern',
        startDate: '2024-02-01',
        endDate: '2024-07-31',
        mentor: 'Dr. Brown'
      }
    },
    { 
      id: 5, 
      name: 'Sakshi', 
      course: 'Computer Science', 
      year: '2nd Year',
      gpa: 3.5,
      internshipStatus: 'Pending',
      performance: {
        technical: 80,
        communication: 78,
        problemSolving: 82,
        teamwork: 85
      },
      internshipDetails: {
        company: 'Data Analytics Corp',
        position: 'Data Science Intern',
        startDate: '2024-06-01',
        endDate: '2024-09-30',
        mentor: 'Dr. Davis'
      }
    }
  ];

  // Handle button clicks
  const handleReviewApplications = () => {
    setActiveSection('applications');
    setShowApplicationsModal(true);
    console.log('Opening applications review section');
  };

  const handleGenerateReports = () => {
    setActiveSection('reports');
    setShowReportsModal(true);
    console.log('Opening reports generation section');
  };

  const handleManageStudents = () => {
    setActiveSection('students');
    setShowStudentsModal(true);
    console.log('Opening student management section');
  };

  const handleSelectStudent = (student) => {
    setSelectedStudent(student);
    // Generate report data for the selected student
    const report = {
      studentName: student.name,
      course: student.course,
      year: student.year,
      gpa: student.gpa,
      internshipStatus: student.internshipStatus,
      performance: student.performance,
      internshipDetails: student.internshipDetails,
      reportDate: new Date().toLocaleDateString(),
      facultyName: 'Dr. Faculty Advisor',
      recommendations: generateRecommendations(student)
    };
    setStudentReports(report);
  };

  const generateRecommendations = (student) => {
    const recommendations = [];
    
    // GPA-based recommendations
    if (student.gpa < 3.5) {
      recommendations.push('Focus on improving academic performance to meet internship requirements');
    }
    
    // Performance-based recommendations
    if (student.performance.technical < 85) {
      recommendations.push('Enhance technical skills through additional coursework or projects');
    }
    
    if (student.performance.communication < 80) {
      recommendations.push('Participate in communication workshops or presentation opportunities');
    }
    
    if (student.performance.problemSolving < 85) {
      recommendations.push('Engage in problem-solving exercises and coding challenges');
    }
    
    if (student.performance.teamwork < 85) {
      recommendations.push('Join collaborative projects to improve teamwork skills');
    }
    
    // Status-based recommendations
    if (student.internshipStatus === 'Pending') {
      recommendations.push('Complete all pending application requirements');
    }
    
    return recommendations;
  };

  // Calculate student distribution for pie chart
  const calculateStudentDistribution = () => {
    const distribution = {
      active: 0,
      pending: 0,
      completed: 0
    };

    students.forEach(student => {
      distribution[student.internshipStatus.toLowerCase()]++;
    });

    return {
      labels: ['Active Internships', 'Pending Applications', 'Completed Internships'],
      datasets: [
        {
          data: [distribution.active, distribution.pending, distribution.completed],
          backgroundColor: [
            'rgba(46, 204, 113, 0.8)',  // Green for active
            'rgba(241, 196, 15, 0.8)',  // Yellow for pending
            'rgba(52, 152, 219, 0.8)',  // Blue for completed
          ],
          borderColor: [
            'rgba(46, 204, 113, 1)',
            'rgba(241, 196, 15, 1)',
            'rgba(52, 152, 219, 1)',
          ],
          borderWidth: 1,
        },
      ],
    };
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'bottom',
      },
      title: {
        display: true,
        text: 'Student Internship Distribution',
        font: {
          size: 16,
          weight: 'bold',
        },
      },
    },
  };

  return (
    <div className="faculty-dashboard">
      <h1>Faculty Dashboard</h1>
      <div className="dashboard-content">
        <section className="stats-section">
          <h2>Overview</h2>
          <div className="stats-grid">
            <div className="stat-card">
              <h3>Total Students</h3>
              <p className="stat-number">150</p>
            </div>
            <div className="stat-card">
              <h3>Active Internships</h3>
              <p className="stat-number">45</p>
            </div>
            <div className="stat-card">
              <h3>Pending Approvals</h3>
              <p className="stat-number">12</p>
            </div>
          </div>
          <div className="chart-container">
            <Pie data={calculateStudentDistribution()} options={chartOptions} />
          </div>
        </section>
        
        <section className="actions-section">
          <h2>Quick Actions</h2>
          <div className="action-buttons">
            <button className="btn btn-primary" onClick={handleReviewApplications}>Review Applications</button>
            <button className="btn btn-secondary" onClick={handleGenerateReports}>Generate Reports</button>
            <button className="btn btn-secondary" onClick={handleManageStudents}>Manage Students</button>
          </div>
        </section>

        {/* Applications Modal */}
        {showApplicationsModal && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h3>Review Applications</h3>
              </div>
              <div className="modal-body">
                <p>Application review interface would appear here</p>
              </div>
              <div className="modal-footer">
                <button className="btn btn-primary" onClick={() => setShowApplicationsModal(false)}>Close</button>
              </div>
            </div>
          </div>
        )}

        {/* Reports Modal */}
        {showReportsModal && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h3>Generate Student Reports</h3>
              </div>
              <div className="modal-body">
                {!selectedStudent ? (
                  <div className="student-selection">
                    <h4>Select a Student</h4>
                    <div className="student-list">
                      {students.map(student => (
                        <div 
                          key={student.id} 
                          className="student-item"
                          onClick={() => handleSelectStudent(student)}
                        >
                          <div className="student-info">
                            <span className="student-name">{student.name}</span>
                            <span className="student-status status-{student.internshipStatus.toLowerCase()}">
                              {student.internshipStatus}
                            </span>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                ) : (
                  <div className="student-report">
                    <div className="report-header">
                      <h4>Student Performance Report</h4>
                      <p className="report-date">Generated on: {studentReports.reportDate}</p>
                    </div>
                    
                    <div className="report-section">
                      <h5>Student Information</h5>
                      <div className="info-grid">
                        <div className="info-item">
                          <span className="info-label">Name:</span>
                          <span className="info-value">{studentReports.studentName}</span>
                        </div>
                        <div className="info-item">
                          <span className="info-label">Course:</span>
                          <span className="info-value">{studentReports.course}</span>
                        </div>
                        <div className="info-item">
                          <span className="info-label">Year:</span>
                          <span className="info-value">{studentReports.year}</span>
                        </div>
                        <div className="info-item">
                          <span className="info-label">GPA:</span>
                          <span className="info-value">{studentReports.gpa}</span>
                        </div>
                        <div className="info-item">
                          <span className="info-label">Internship Status:</span>
                          <span className="info-value">{studentReports.internshipStatus}</span>
                        </div>
                      </div>
                    </div>
                    
                    <div className="report-section">
                      <h5>Internship Details</h5>
                      <div className="info-grid">
                        <div className="info-item">
                          <span className="info-label">Company:</span>
                          <span className="info-value">{studentReports.internshipDetails.company}</span>
                        </div>
                        <div className="info-item">
                          <span className="info-label">Position:</span>
                          <span className="info-value">{studentReports.internshipDetails.position}</span>
                        </div>
                        <div className="info-item">
                          <span className="info-label">Duration:</span>
                          <span className="info-value">
                            {studentReports.internshipDetails.startDate} to {studentReports.internshipDetails.endDate}
                          </span>
                        </div>
                        <div className="info-item">
                          <span className="info-label">Faculty Mentor:</span>
                          <span className="info-value">{studentReports.internshipDetails.mentor}</span>
                        </div>
                      </div>
                    </div>
                    
                    <div className="report-section">
                      <h5>Performance Metrics</h5>
                      <div className="skill-bars">
                        <div className="skill-item">
                          <div className="skill-label">Technical Skills</div>
                          <div className="progress">
                            <div 
                              className="progress-bar" 
                              role="progressbar" 
                              style={{ width: `${studentReports.performance.technical}%` }}
                            >
                              {studentReports.performance.technical}%
                            </div>
                          </div>
                        </div>
                        <div className="skill-item">
                          <div className="skill-label">Communication</div>
                          <div className="progress">
                            <div 
                              className="progress-bar" 
                              role="progressbar" 
                              style={{ width: `${studentReports.performance.communication}%` }}
                            >
                              {studentReports.performance.communication}%
                            </div>
                          </div>
                        </div>
                        <div className="skill-item">
                          <div className="skill-label">Problem Solving</div>
                          <div className="progress">
                            <div 
                              className="progress-bar" 
                              role="progressbar" 
                              style={{ width: `${studentReports.performance.problemSolving}%` }}
                            >
                              {studentReports.performance.problemSolving}%
                            </div>
                          </div>
                        </div>
                        <div className="skill-item">
                          <div className="skill-label">Teamwork</div>
                          <div className="progress">
                            <div 
                              className="progress-bar" 
                              role="progressbar" 
                              style={{ width: `${studentReports.performance.teamwork}%` }}
                            >
                              {studentReports.performance.teamwork}%
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                    
                    <div className="report-section">
                      <h5>Recommendations</h5>
                      <ul className="recommendations-list">
                        {studentReports.recommendations.map((recommendation, index) => (
                          <li key={index}>{recommendation}</li>
                        ))}
                      </ul>
                    </div>
                    
                    <div className="report-footer">
                      <p className="faculty-signature">Faculty Advisor: {studentReports.facultyName}</p>
                    </div>
                  </div>
                )}
              </div>
              <div className="modal-footer">
                {selectedStudent && (
                  <button 
                    className="btn btn-secondary" 
                    onClick={() => setSelectedStudent(null)}
                  >
                    Back to Student List
                  </button>
                )}
                <button className="btn btn-primary" onClick={() => setShowReportsModal(false)}>Close</button>
              </div>
            </div>
          </div>
        )}

        {/* Students Modal */}
        {showStudentsModal && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h3>Manage Students</h3>
              </div>
              <div className="modal-body">
                <p>Student management interface would appear here</p>
              </div>
              <div className="modal-footer">
                <button className="btn btn-primary" onClick={() => setShowStudentsModal(false)}>Close</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default FacultyDashboard;
