import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Your backend API URL

export const mongodbService = {
    // Save student application
    saveApplication: async (applicationData) => {
        try {
            const response = await axios.post(`${API_URL}/applications`, applicationData);
            return response.data;
        } catch (error) {
            console.error('Error saving application:', error);
            throw error;
        }
    },

    // Get student applications
    getApplications: async (studentId) => {
        try {
            const response = await axios.get(`${API_URL}/applications/student/${studentId}`);
            return response.data;
        } catch (error) {
            console.error('Error fetching applications:', error);
            throw error;
        }
    },

    // Update application status
    updateApplicationStatus: async (applicationId, status) => {
        try {
            const response = await axios.put(`${API_URL}/applications/${applicationId}/status`, { status });
            return response.data;
        } catch (error) {
            console.error('Error updating application status:', error);
            throw error;
        }
    }
};
